                    var elem_waktu = el[1].split(':');
                    var tahun = elem_tgl[0];
                    var bulan = elem_tgl[1];
                    var hari = elem_tgl[2];
                    return hari + '/' + bulan + '/' + tahun + ' ' + elem_waktu[0] + ':' + elem_waktu[1];

                }

                function formatTanggalRTIND(waktu) {

                    var elem_tgl = waktu.split('-');
