                $sirs_tanggal_p = $new;
            }

            $surveilans['sirs_tanggal_ttd_perawat'] = $sirs_tanggal_p;

            $sirs_tanggal_ipcn = safe_post('surveilans_tanggal_ttd_ipcn');

