                $var1 = explode("/", $sirs_tanggal_ipcn);

                $var2 = explode(" ", $var1[2]);

                $var3 = $var2[0] . "-" . $var1[1] . "-" . $var1[0] . " " . $var2[1];

                $date = new DateTime($var3);

                $new = $date->format('Y-m-d H:i:s');


