        $cek_resep = $this->db->select('count(*) as count')->from('sm_resep')->where('id_layanan_pendaftaran', $id_layanan_pendaftaran, true)->get()->row()->count;
        if (safe_post('tindak_lanjut') !== 'Pemulasaran Jenazah') :
