    function curlLAB_get()
    {

        if (!$this->get('ono') && !$this->get('id_lab')) :
            $this->response(NULL, REST_Controller::HTTP_BAD_REQUEST);
        endif;
        $id_ono         = $this->get('ono');
        $id_lab         = $this->get('id_lab');

