    // LMDT
    function lembar_monitoring_delete($id)
    {
        $status = $this->m_pelayanan->deleteLembarMonitoring($id);
        if ($status) :
            $response = array('status' => $status, 'message' => 'Berhasil menghapus Lembar Monitoring!');
