    {
        if (!$this->get('id', true)) :
            $this->response(null, REST_Controller::HTTP_BAD_REQUEST);
        endif;
        // var_dump($this->get('id'));die;
        $data = $this->m_pelayanan->getLembarMonitoringByID($this->get('id', true));
